adb shell content query --uri content://com.android.contacts/data > /tmp/test ; echo "Contacted_times , Display_name , Sent Via" ; cat  /tmp/test  | awk '{ delete vars; for(i = 1; i <= NF; ++i) { n = index($i, "="); if(n) { vars[substr($i, 1, n - 1)] = substr($i, n + 1) } } name = vars["display_name"] ;Var = vars["data1"] ; var2 = vars["times_contacted"] } { print  name " -> " var2 " " Var  }' |uniq  | awk '{print $3 $1 "    \t" $4  }' |sort -rn |grep +91 |uniq -w 10|head -n11 | sed  's/,/\t/g'
